mapper.select:
SELECT p.ci_profissional, p.ds_url_imagem, um.ci_usuario_mobile, um.ds_nome, l.ci_localizacao, l.nr_latitude, l.nr_longitude, sp.ci_servico_profissional, sp.nm_servico_profissional, sp.ds_servico_profissional, sp.nr_tempo, sp.nr_valor, e.ds_especialidade, e.ci_especialidade, sc.ci_sub_categoria, sc.ds_sub_categoria, c.ci_categoria, c.ds_categoria, count(pfu.ci_profissional_favorito_usuario) nr_favoritos, (select true from tb_profissional_favorito_usuario pf where pf.cd_usuario_mobile = ${idUsuario} and pf.cd_profissional = p.ci_profissional) fl_favorito FROM tb_profissional p INNER JOIN tb_localizacao l ON l.ci_localizacao = p.cd_localizacao INNER JOIN tb_servico_profissional sp ON sp.cd_profissional = p.ci_profissional LEFT JOIN tb_profissional_favorito_usuario pfu ON pfu.cd_profissional = p.ci_profissional AND pfu.fl_favorito = 1 INNER JOIN tb_usuario_mobile um ON um.ci_usuario_mobile = p.cd_usuario INNER JOIN tb_especialidade e ON e.ci_especialidade = p.cd_especialidade INNER JOIN tb_sub_categoria sc ON sc.ci_sub_categoria = p.cd_sub_categoria INNER JOIN tb_categoria c ON c.ci_categoria = p.cd_categoria WHERE 1 = 1
AND (um.ds_nome ilike ${pesquisaToolbarLike} OR sp.nm_servico_profissional ilike ${pesquisaToolbarLike} OR sp.ds_servico_profissional ilike ${pesquisaToolbarLike} OR e.ds_especialidade ilike ${pesquisaToolbarLike} OR sc.ds_sub_categoria ilike ${pesquisaToolbarLike} OR c.ds_categoria ilike ${pesquisaToolbarLike} )
AND c.ci_categoria = ${idCategoria}
AND sc.ci_sub_categoria = ${idSubCategoria}
AND e.ds_especialidade = ${idEspecialidade}
GROUP BY p.ci_profissional, p.ds_url_imagem, um.ci_usuario_mobile, um.ds_nome, l.ci_localizacao, l.nr_latitude, l.nr_longitude, sp.ci_servico_profissional, sp.nm_servico_profissional, sp.ds_servico_profissional, sp.nr_tempo, sp.nr_valor, e.ds_especialidade, e.ci_especialidade, sc.ci_sub_categoria, sc.ds_sub_categoria, c.ci_categoria, c.ds_categoria
ORDER BY sp.ci_servico_profissional DESC
,sp.nr_valor ASC ,sp.nr_valor DESC
, um.ds_nome ASC ,um.ds_nome DESC


			SELECT 
				ci_profissional_favorito_usuario id
  			FROM tb_profissional_favorito_usuario
  			WHERE cd_usuario_mobile = #{idUsuario}
  			AND cd_profissional = #{idProfissional}
